changeSet.sql:
UPDATE ap_binding_state a SET external_system_id = b.external_system_id
            FROM (SELECT external_system_id, binding_id FROM ap_binding) b
            WHERE a.binding_id = b.binding_id;

CREATE UNIQUE INDEX ap_binding_state_access_point_idx 
            ON ap_binding_state(access_point_id,external_system_id)
			WHERE delete_change_id IS NULL;

CREATE UNIQUE INDEX arr_level_parent_position_idx 
            ON arr_level(node_id_parent, "position")
			WHERE delete_change_id IS NULL AND node_id_parent IS NOT NULL;

UPDATE arr_item SET item_spec_id = 
				(SELECT item_spec_id FROM rul_item_spec WHERE code = 'ZP2015_ENTITY_ROLE_11')
				WHERE item_spec_id = (SELECT item_spec_id FROM rul_item_spec WHERE code = 'ZP2015_ENTITY_ROLE_1');

UPDATE arr_node_conformity_missing SET item_spec_id = 
				(SELECT item_spec_id FROM rul_item_spec WHERE code = 'ZP2015_ENTITY_ROLE_11')
				WHERE item_spec_id = (select item_spec_id from rul_item_spec WHERE code = 'ZP2015_ENTITY_ROLE_1');

